function resultHandler(e){result=statement.getResult();statement.removeEventListener(air.SQLEvent.RESULT,resultHandler)}function openHandler(){statement=new air.SQLStatement;statement.sqlConnection=conn;statement.text="SELECT * FROM ser_id";statement.addEventListener(air.SQLEvent.RESULT,resultHandler);statement.execute()}function appLoad(){$(function(){$("#radio").buttonset()});var e=air.File.applicationDirectory,t=e.resolvePath("test.db");conn.addEventListener(air.SQLEvent.OPEN,openHandler,!1);conn.openAsync(t)}function loadData(e){get_series(e)}function get_series(e){var t;statement.text="SELECT * FROM ser_id WHERE g_id LIKE "+e;statement.addEventListener(air.SQLEvent.RESULT,g_idResultHandler,!1);statement.execute()}function g_idResultHandler(e){result=statement.getResult();s_id=result.data[0].series_id;statement.removeEventListener(air.SQLEvent.RESULT,g_idResultHandler);statement.text="SELECT * FROM ser_data WHERE ser_id LIKE '"+s_id+"'";statement.addEventListener(air.SQLEvent.RESULT,s_idResultHandler,!1);statement.execute()}function s_idResultHandler(e){result=statement.getResult();data=JSON.parse(result.data[0].observations);statement.removeEventListener(air.SQLEvent.RESULT,s_idResultHandler);var t="DATA:";$("#result").html("");$.each(data,function(e,t){$("#result").append("<p>"+data[e].value+"</p>")})}var result,statement;alert("functions are fun");var conn=new air.SQLConnection;